var signs = {
  ROCK: 0,
  PAPER: 1,
  CISSORS: 2,
  LIZARD: 3,
  SPOCK: 4
};

var rules = {
  rock: function(sign) {
    return sign == signs.CISSORS || sign == signs.LIZARD;
  },
  paper: function(sign) {
    return sign == signs.ROCK || sign == signs.SPOCK;
  },
  cissors: function(sign) {
    return sign == signs.PAPER || sign == signs.LIZARD;
  },
  lizard: function(sign) {
    return sign == signs.PAPER || sign == signs.SPOCK;
  },
  spock: function(sign) {
    return sign == signs.ROCK || sign == signs.CISSORS;
  },
};

function Sign(sign) {
  switch (sign) {
  case signs.ROCK:
    this.wins = rules.rock;
    break;
  case signs.PAPER:
    this.wins = rules.paper;
    break;
  case signs.CISSORS:
    this.wins = rules.cissors;
    break;
  case signs.LIZARD:
    this.wins = rules.lizard;
    break;
  case signs.SPOCK:
    this.wins = rules.spock;
    break;
  default:
    return;
  }
}

/**
 * Decides if the sign played wins vs against.
 * @param played sign played
 * @param against sign of the opponent
 * @return true if @a played wins @a against
 */
function play(played, against) {
  return (new Sign(played)).wins(against);
}

// Exports
exports.signs = signs;
exports.play = play;
